package testPackage1import org.apache.flink.streaming.api.scala._class testS1 {  val env = StreamExecutionEnvironment.getExecutionEnvironment  val socketStream = env.socketTextStream("localhost",9000)  val wordsStream = socketStream.flatMap(value => value.split("\\s+")).map(value => (value,1))  val keyValuePair = wordsStream.keyBy(0)  val countPair = keyValuePair.sum(1)  countPair.print()  env.execute()}